import axios from 'axios'
import React, { useEffect,useState } from 'react'
import { Link } from 'react-router-dom';

export default function Read() {
  const [data, setdata] = useState([])

function getData(){
  axios.get('https://63ea415e811db3d7ef0a6c6d.mockapi.io/crud').then(response=> setdata(response.data));

}

useEffect(()=>{
getData();
},[]);

// function handleDelete(id){
// axios.delete(`https://63ea415e811db3d7ef0a6c6d.mockapi.io/crud/${id}`).then(()=> {getData()});
// }

function handleDelete(id){
  axios.delete(`https://63ea415e811db3d7ef0a6c6d.mockapi.io/crud/${id}`).then(()=>{
  getData();

  })

}
  return (
    <>
  <div className="heading">
		<h1>Students  Attendance Management System</h1>
	</div>
  <div className="main">
  <Link to={'/Create'}>
  <button className='btn btn-info mt-5 ml-5' >ADD STUDENT+</button>

  </Link>
	<h1> STUDENT  RECORD</h1>

</div>
      <div className='container'>
      <div className='butt'>
      

      </div>
      <table className='table table-bordered table-striped table-dark  table-hover ' >
        <thead >
            <tr>
                <th>ID</th>  
                <th>Fname</th>
                <th>Lname</th>
                <th>Number</th>
                <th>D.O.B</th>
                <th>Department</th>
                <th>EDIT</th>
                <th>DELETE</th>
            </tr>
        </thead>
        <tbody>
        {
  data.map((currData)=>{
    return(
      <tr>
      <td>{currData.my_id}</td>
      <td>{currData.f_name}</td>
      <td>{currData.l_name}</td>
      <td>{currData.my_num}</td>
      <td>{currData.my_dob}</td>
      <td>{currData.my_dept}</td>

      <td>
      <button className='btn  m-0 btn-primary' > EDIT</button>

      </td>
      

      {/* <td> <button className='btn m-0 btn-danger'  onClick={()=>handleDelete(currData.my_id)}> DELETE</button> </td> */}
      {/* <td> <button className='btn m-0 btn-danger'  onClick={()=>{if(window.confirm("are you sure")){handleDelete(currData.id)}}}> DELETE</button> </td> */}
      <td> <button className='btn m-0 btn-danger'  onClick={()=>{if(window.confirm("are you sure")){handleDelete(currData.my_num)}}}> DELETE</button> </td>





  </tr> 

    )

  })
}
                 
        </tbody>
      </table>
      
      </div>
    </>
  )
}
