// Note:- in this  progran i have  stuck bcz of  this line "setdata(data.Search))" in which i have used search in small letter "s" insted of capital leter "S"
import React, { useState } from 'react'
import {css} from "./MovieApp.css"
import MovieCard from "./MovieCard";


export default function MovieAppContainer() {

const[data,setdata]=useState([])
const[search,setsearch]=useState("");



  function myfun(e){
e.preventDefault();
let sam=fetch(`https:www.omdbapi.com/?apikey=e70daa5a&s=${search}`).then((response) => response.json()).then((data) => setdata(data.Search))

}
  
  return (
    <>
    <div className='bigcon'>
      <div className='container'>
      <h1>Movie Search App</h1>
        <form className='box' onSubmit={myfun}>
        <input value={search} onChange={(e)=>{setsearch(e.target.value)}}  />
        <button type='submit' value="search" onClick={()=>alert('you just clicked')}>search</button>
        </form>
      </div>
      {  data.length>=1 ? <MovieCard mydata={data}/>:null}{/* condition? if true: if false */}
    
    </div>
    </>
  )
}
